(function(window, undefined) {
  var $console_check = function() {
    if(window === this) {
      return new $console_check();
    }
    
    //Map in case of overwrite
    _$console_check = window._$console_check;
    
    return this;
  };
  
  $console_check.fn = $console_check.prototype = {
    checkExistence: function() {
      if(!window.console) {
        window.console = {
          assert: function() {},
          clear: function() {},
          count: function() {},
          debug: function() {},
          dir: function() {},
          dirxml: function() {},
          error: function() {},
          group: function() {},
          groupCollapsed: function() {},
          groupEnd: function() {},
          info: function() {},
          log: function() {},
          profile: function() {},
          profileEnd: function() {},
          time: function() {},
          timeEnd: function() {},
          timeStamp: function() {},
          trace: function() {},
          warn: function() {}
        };
      }
    }
  };
  
  window.$console_check = $console_check;
})(window);
