(function(window, undefined) {
  var $console_check = function() {
    if(window === this) {
      return new $console_check();
    }
    
    //Map in case of overwrite
    _$console_check = window.$console_check;
    
    return this;
  };
  
  $console_check.fn = $console_check.prototype = {
    checkExistence: function(msgArr) {
      if(!window.console) {
        window.console = {
          assert: function() {},
          clear: function() {},
          count: function() {},
          debug: function() {},
          dir: function() {},
          dirxml: function() {},
          error: function() {},
          group: function() {},
          groupCollapsed: function() {},
          groupEnd: function() {},
          info: function() {},
          log: function() {},
          profile: function() {},
          profileEnd: function() {},
          time: function() {},
          timeEnd: function() {},
          timeStamp: function() {},
          trace: function() {},
          warn: function() {}
        };
        
        if(!(Array.isArray(msgArr))) {
          if(msgArr instanceof Set || msgArr instanceof Map || msgArr instanceof Object) {
            Array.from(msgArr);
          } else {
            throw new Error("Argument is of wrong type");
          }
        } else if(Array.isArray(msgArr) && msgArr.length !== 2) {
          throw new Error("Argument length is of wrong amount. You need to have two elements.");
        } else {
          window.alert(msgArr[0]); // needs to tell client that window.console is finally defined
        }
      } else {
        window.alert(msgArr[1]); // needs to tell client that window.console is already defined
      }
    }
  };
  
  window.$console_check = $console_check;
})(window);
